package nl.overheid.koop.plooi.plooisecurity.domain.authorization;

import lombok.val;
import org.junit.jupiter.api.Test;
import org.springframework.security.core.authority.SimpleGrantedAuthority;
import org.springframework.security.core.context.SecurityContextHolder;

import static org.junit.jupiter.api.Assertions.*;

class SecurityContextInitializerTest {

    @Test
    void setSecurityContext() {
        val token = "";
        val sut = new SecurityContextInitializer();

        sut.setSecurityContext(token);

        val authentication = (JwtAuthenticationToken)SecurityContextHolder.getContext().getAuthentication();
        assertEquals("mnre1034", authentication.getOrganisations().get(0));
        assertTrue(authentication.getAuthorities().contains(new SimpleGrantedAuthority("Publisher KST")));
    }

    @Test
    void setSecurityContext_unableToParseToken() {
        val token = "";
        val sut = new SecurityContextInitializer();

        val exception = assertThrows(RuntimeException.class, () -> sut.setSecurityContext(token));
        assertEquals("Unable to set the Security Context", exception.getMessage());
    }
}
